-- Schema
DROP TABLE IF EXISTS job_posting_tags;
DROP TABLE IF EXISTS job_posting;
CREATE TABLE job_posting (id BIGINT PRIMARY KEY AUTO_INCREMENT,position_name VARCHAR(255),organization_title VARCHAR(255),organization_url VARCHAR(512),logo_url VARCHAR(512),labor_function VARCHAR(255),location VARCHAR(255),description_html TEXT,posted_date DATE,job_page_url VARCHAR(512));

CREATE TABLE job_posting_tags (job_posting_id BIGINT,tag VARCHAR(255),FOREIGN KEY (job_posting_id) REFERENCES job_posting(id));

